INSERT INTO `shops_rows` VALUES (1,11,1,NULL,NULL,'TỦ ĐIỆN ĐIỀU KHIỂN','tu-dien-dieu-khien','instock','TUDIEUKHIEN',0,0,0,0,0,0,'1697034196169.jpg','',NULL,NULL,'Tủ điện có vai trò điều khiển nhiệt độ kho lạnh  tùy theo ý của người sử dụng.  Ngoài ra tủ điện hiển thị nhiệt độ phòng, hiển thị hoạt động cũng như báo lỗi của các thiết bị thông hệ thống lạnh. Kích thước tủ điện công nghiệp phù hợp với tiêu chuẩn sử dụng','<p>Tủ điện điều khiển kho lạnh<br />\r\nTủ điện có vai trò điều khiển nhiệt độ kho lạnh&nbsp; tùy theo ý của người sử dụng.&nbsp;</p>\r\n\r\n<p>Ngoài ra tủ điện hiển thị nhiệt độ phòng, hiển thị hoạt động cũng như báo lỗi của các thiết bị thông hệ thống lạnh.</p>\r\n\r\n<p>Kích thước tủ điện công nghiệp phù hợp với tiêu chuẩn sử dụng</p>\r\n\r\n<p>Tủ điện điều khiển kho lạnh được kho lạnh Hưng Tín&nbsp;phát triển, sản xuất đem lại hiệu quả tốt nhất cho sử dụng kho lạnh.</p>\r\n\r\n<p><img alt=\"\" src=\"https://kholanhchatluongcao.com/ckeditor/kcfinder/upload/images/1697034196169.jpg\" style=\"width: 700px; height: 604px;\" /></p>\r\n\r\n<p>Đặc điểm kỹ thuật củ tủ điện điều khiển kho lạnh<br />\r\nVỏ tủ điện công nghiệp làm bằng thép tấm, sơn tĩnh điện chống rỉ sét, ăn mòn.<br />\r\nHệ thống hiển thị hoạt đông, bá lỗi sử dụng bằng đèn báo led.<br />\r\nSử dụng thiết bọ điều khiển nhiệt độ: Dixell, Eliwell, Danfoss, ED 108, Fox….<br />\r\nThiết bị điện: CB, cầu trì, bảo vệ pha, timer, khởi động từ, domino…từ các thương hiệu như: LS, Chint, Schneider….<br />\r\nNguồn điện: 220V và 380V.</p>\r\n\r\n<p><img alt=\"\" src=\"https://kholanhchatluongcao.com/ckeditor/kcfinder/upload/images/1697034196042.jpg\" style=\"width: 766px; height: 857px;\" /><br />\r\nƯu điểm của tủ điện điều khiển kho lạnh<br />\r\nTủ điện được bố trí hợp lý đẹp mắt theo tiêu chuẩn điện công nghiệp, dễ dàn bảo trì bảo hành.<br />\r\nBảo vệ nguồn điện: mất pha, lệch pha, điện áp tăng quá cao hoặc quá thấp. dễ dàng chỉnh dòng điện.<br />\r\nLập trình tự động hóa cho máy nén, quạt dàn lạnh, chu kỳ xả băng dàn lạnh.<br />\r\nCảnh báo sự cố qua hiển thị đèn Led: báo lỗi pha, lỗi máy nén, lỗi xả đá, lỗi quạt dàn lạnh…<br />\r\nThiết kế cho hệ thống lạnh có công suất từ 1hp tới 100hp tùy theo nhu cầu của khách hàng.</p>\r\n','TỦ ĐIỆN ĐIỀU KHIỂN CHILBLOCK','','TỦ ĐIỆN ĐIỀU KHIỂN CHILBLOCK','','TỦ ĐIỆN ĐIỀU KHIỂN CHILBLOCK',0,1,0,0,0,1,405,1,1,1692070588,1697342369),(2,16,1,NULL,NULL,'MÁY NÉN BITZER MODEL 4JE-15Y-40P CÔNG SUT 15.0 HP','may-nen-bitzer-model-4je-15y-40p-cong-sut-15-0-hp','instock','MODEL 4JE-15Y-40P',0,0,0,0,0,0,'thienlocphat_asia_ctc_t1546223162_tHAz.jpg','',NULL,NULL,'Miễn phí vận chuyển cho đơn hàng từ 1.000.000đ trở lên\r\nThanh toán trực tiếp khi nhận hàng\r\nĐổi trả hàng trong vòng 7 ngày','<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"width: 828px; font-size: 16px; background: rgb(255, 255, 255); margin: 0px; padding: 0px; list-style: none; color: rgb(27, 25, 25); font-family: Arial, Tahoma, sans-serif;\" width=\"828\">\r\n	<tbody style=\"margin: 0px; padding: 0px; list-style: none;\">\r\n		<tr style=\"border: 1px solid rgb(197, 197, 197); background: rgb(255, 248, 18); margin: 0px; padding: 0px; list-style: none; border-collapse: collapse;\">\r\n			<td style=\"border: 1pt solid rgb(238, 238, 238); padding: 6pt; margin: 0px; list-style: none; border-collapse: collapse; width: 198px; vertical-align: top;\">\r\n			<p style=\"margin-bottom: 0.0001pt; font-size: 17px; font-weight: 700; padding: 0px; list-style: none; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; font-size: 14px;\"><span style=\"margin: 0px; padding: 0cm; list-style: none; border: 1pt none windowtext; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; fo